library ieee;
use ieee.std_logic_1164.all;
use ieee.std_logic_arith.all;
use ieee.std_logic_unsigned.all;

entity counter is
   port( 
      cnt   : out    std_logic_vector (31 downto 0);
      reset : in     std_logic;
      clk   : in     std_logic
   );

-- Declarations

end counter ;

