architecture rtl of counter is

   -- Architecture declarations
     signal counter: std_logic_vector(31 downto 0);

begin

   -----------------------------------------------------------------
   process (clk)
   -----------------------------------------------------------------
   begin
      if  rising_edge(clk)  then
         if  reset = '1'  then
            counter <= (others => '0');
         else
            counter <= counter +1;
         end if;
      end if;
   end process;
  cnt<= counter;

end rtl;
